<script>
    export default {
        name: "Warp",
        data() {
            return {
                nightTime: false,
                daytime: false
            }
        },
        created() {
            this.getNowTime();
        },
        methods: {
            getNowTime() {
                var time = new Date().getHours();
                if (time >= 6 && time <= 18){
                    this.nightTime = !this.nightTime;
                }
                else {
                    this.daytime = !this.daytime;
                }
            }
        }
    }
</script>

